def get_accept_trial_image():
    """Accept Trial butonunun base64'ünü OpenCV formatına çevirir"""
    image_data = base64.b64decode(ACCEPT_TRIAL_IMAGE_B64)
    image = Image.open(io.BytesIO(image_data))
    return cv2.cvtColor(np.array(image), cv2.COLOR_RGB2BGR)

